/*
Function needs to be initialized with the `setInterval()` and not the mutation observer.

Instructions: 
- Initialize the function and add 2 parameters to it: selector and min
 - example: textFitMin('.header', 20)
 - selector is the CSS selector of the element you want to affect (and also the element that has textFit function applied to it as well)
 - min is the minimum font size (in px) that you want to allow
*/

function textFitMin(selector, min) {
  const targets = document.querySelectorAll(selector);

  targets.forEach(target => {
    const computedFontSize = parseInt(window.getComputedStyle(target).fontSize);
    
    if ( computedFontSize < min) {
      target.classList.add('overflow');
    } else {
      target.classList.remove('overflow');
    }
  });
}
